def solve(n):
    count=0
    x=n+1
    z=n+1
    if x%2==0:
        x=x//2
    else:
        x=(x//2)+1
     
    a=[]
    a.append(1)
    a.append(n)
       
    for i in range(2,x):
        p=1
        y=1 
        for j in range(n,(n-i),-1):
            count+=1
            p*=j
         
        for k in range(2,i+1):
            y*=k  
        a.append((p//y)%1000000000)  
    if z%2==0:
        for i in range(len(a)-1,-1,-1):
            a.append(a[i])   
    else:
        for i in range(len(a)-2,-1,-1):
            a.append(a[i])

    b=[]
    b.append(1)
    b.append(n)
    comb=n
    print("------------------------------------")
    for i in range(2,n+1):
        print(comb,end="  ")
        comb=(((n+1-i)*comb)//i)%10**9
        b.append(comb)
        
    
    print("---------------------------------------------") 
    print(a)   
    print(b)
    print(a==b)
    
    
    
solve(5)    